import React from "react";
import "./Card.css";
const Card = ({ card, handleChoice, flipped, disable }) => {
  const handleClick = () => {
    handleChoice(card);
  };
  return (
    <div key={card.id} className="card">
      <div className={flipped ? "flipped" : ""}>
        <img src={card.src} alt={card.src} className="front" />
        <img
          src="/img/cover.png"
          alt="cover"
          className="back"
          onClick={handleClick}
        />
      </div>
    </div>
  );
};

export default Card;
